<script>
import { computed, defineComponent } from "vue";
import { colors, useQuasar } from "quasar";

export default defineComponent({
  name: "EffectIcon",
  props: {
    modelValue: {
      type: String,
      required: true,
      default: () => "breathing"
    },
    disabled: {
      type: Boolean,
      default: false
    },
    fill: {
      type: String,
      default: () => "#bcbcbc"
    }
  },
  setup(props) {
    const $q = useQuasar();
    const { getPaletteColor } = colors;
    const fillColor = computed(() => {
      if (props.disabled) {
        return getPaletteColor("grey");
      }
      if (props.fill === "") {
        return $q.dark.isActive
          ? getPaletteColor("grey-3")
          : getPaletteColor("grey-8");
      }
      return props.fill;
    });
    return { fillColor };
  }
});
